		for (String line: exampleLines) sb.append( line ).append( '\n' );
		Model m = ModelIOUtils.modelFromTurtle( sb.toString() );
		long start = System.currentTimeMillis();
		CycleFinder.findCycles( m.createResource( "http://training.epimorphics.com/transport/london-underground/line/jubilee" ) );
